import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const Login = () => {
  const navigate = useNavigate();

  const [loginData, setLoginData] = useState({
    username: "",
    password: "",
  });

  const [showLoginPassword, setShowLoginPassword] = useState(false);

  const handleLoginInputChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const toggleLoginPasswordVisibility = () => {
    setShowLoginPassword(!showLoginPassword);
  };

  const handleAcceder = async () => {
    const { username, password } = loginData;

    if (!username || !password) {
      alert("Por favor, complete todos los campos.");
      return;
    }

    try {
      const response = await fetch(
        "https://bancoastralis-api.up.railway.app/api/v1/auth/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            user: username,   //  Lo que tu API espera
            password: password
          }),
        }
      );

      const data = await response.json();

      if (!response.ok) {
        alert(data.message || "Credenciales incorrectas");
        return;
      }

      alert("Inicio de sesi贸n exitoso ");

      if (data.token) {
        localStorage.setItem("token", data.token);
      }

      setLoginData({ username: "", password: "" });

      navigate("/dashboard");

    } catch (err) {
      console.error("Error en login:", err);
      alert("Error de conexi贸n con el servidor.");
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesi贸n</h2>

      <input
        type="text"
        name="username"
        placeholder="Usuario o correo"
        value={loginData.username}
        onChange={handleLoginInputChange}
      />

      <div className="password-input-container">
        <input
          type={showLoginPassword ? "text" : "password"}
          name="password"
          placeholder="Contrase帽a"
          value={loginData.password}
          onChange={handleLoginInputChange}
        />
        <button
          type="button"
          className="password-toggle-btn"
          onClick={toggleLoginPasswordVisibility}
          aria-label={showLoginPassword ? "Ocultar contrase帽a" : "Mostrar contrase帽a"}
        >
          {showLoginPassword ? <FaEyeSlash /> : <FaEye />}
        </button>
      </div>

      <button className="login-btn" onClick={handleAcceder}>
        Acceder
      </button>
    </div>
  );
};

export default Login;
